//SPDX-License-Identifier: MIT


pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract CrowdFund{

    event Launch(
        uint id,
        address indexed creator,
        uint goal,
        uint32 startAt,
        uint32 endAt
    );

    event Cancel(uint id);

    event Pledge(uint indexed id, address indexed caller, uint amount);

    event Unpledge(uint indexed id, address indexed caller, uint amount);

    event Claim(uint id);
    event Refund(uint indexed id, address indexed caller, uint amount);

    struct Campaign{
    address creator;
    uint goal;
    uint pledged;
    uint32 startAt;
    uint32 endAt;
    bool claimed;
    }

    IERC20 public immutable token;
    uint public count;
    mapping(uint=>Campaign) public campaigns;
    mapping(uint=>mapping(address=>uint)) public pledgedAmount;

    constructor(address _token){
        token = IERC20(_token);
    }

    function launch(uint _goal, uint32 _startAt, uint32 _endAt) external {
        require(_startAt>=block.timestamp,"start At< now");
        require(_endAt>=_startAt, "end at> max duration");
        require(_endAt<=block.timestamp+90 days,"end At should be less or equal to 90 days after start date");

        count+=1;
        campaigns[count] = Campaign({
            creator: msg.sender,
            goal: _goal,
            pledged: 0,
            startAt: _startAt,
            endAt: _endAt,
            claimed: false
        });

        emit Launch(count, msg.sender, _goal, _startAt, _endAt);

    }

    function cancel(uint _id) external{
        Campaign memory campaign = campaigns[_id];
        require(msg.sender==campaign.creator, "You don't have access to cancel the campaign");

        require(block.timestamp<campaign.startAt,"Started");

        delete campaigns[_id];

        emit Cancel(_id);

    }

    function pledge(uint _id, uint _amount) external {
        Campaign storage campaign = campaigns[_id];
        require(block.timestamp>=campaign.startAt,"Not Started");

         require(block.timestamp<=campaign.endAt,"Ended");

         campaign.pledged+=_amount;
         pledgedAmount[_id][msg.sender]=_amount;
         token.transferFrom(msg.sender, address(this), _amount);

         emit Pledge(_id, msg.sender, _amount);
    }

    function unpledge(uint _id, uint _amount) external {
        Campaign storage campaign = campaigns[_id];
        require(block.timestamp>=campaign.startAt,"Not Started");

         require(block.timestamp<=campaign.endAt,"Ended");

         require(campaign.pledged>=_amount,"Amount should be less or equal to pledged amount");

         campaign.pledged-=_amount;
         pledgedAmount[_id][msg.sender]-=_amount;
         token.transfer(msg.sender,  _amount);

         emit Unpledge(_id, msg.sender, _amount);
    }

    function claim(uint _id) external{
        Campaign storage campaign = campaigns[_id];
        require(msg.sender==campaign.creator, "Not creator");
        require(campaign.pledged>=campaign.goal,"Pledged<goal");
        require(block.timestamp>campaign.endAt,"Not ended");
        require(!campaign.claimed, "Claimed");
        campaign.claimed = true;
        token.transfer(msg.sender, campaign.pledged);
        emit Claim(_id);
    }

    function refund(uint _id) external{
         Campaign storage campaign = campaigns[_id];
        require(campaign.pledged<campaign.goal,"Pledged<goal");
        require(block.timestamp>campaign.endAt,"Not ended");

        uint bal = pledgedAmount[_id][msg.sender];
        pledgedAmount[_id][msg.sender] = 0;
        token.transfer(msg.sender,bal);

        emit Refund(_id, msg.sender, bal);
    }
}

